import React from "react";
import useInput from "../hooks/use-input";

const BasicForm = (props) => {
  // const isNotEmpty = () => {
  //   return
  // }
  const {
    value: firstNameEnteredValue,
    hasError: firstNameHasError,
    isValid: firstNameValueIsValid,
    reset: resetFirstName,
    valueChangeHandler: firstNameInputChangeHandler,
    inputBlurHandler: firstNameInputBlurHandler,
  } = useInput((value) => {
    return value.trim() !== "";
  });

  const {
    value: lastNameEnteredValue,
    hasError: lastNameHasError,
    isValid: lastNameValueIsValid,
    reset: resetLastName,
    valueChangeHandler: lastNameInputChangeHandler,
    inputBlurHandler: lastNameInputBlurHandler,
  } = useInput((value) => {
    return value.trim() !== "";
  });

  const {
    value: emailEnteredValue,
    hasError: emailHasError,
    isValid: emailValueIsValid,
    reset: resetEmail,
    valueChangeHandler: emailInputChangeHandler,
    inputBlurHandler: emailInputBlurHandler,
  } = useInput((value) => {
    return value.trim().includes();
  });

  const firstNameIsInvalid = firstNameValueIsValid && firstNameHasError;
  const lastNameIsInvalid = lastNameValueIsValid && lastNameHasError;
  const emailIsInvalid = emailValueIsValid && emailHasError;

  const onSubmitHandler = () => {
    resetFirstName();
    resetLastName();
    resetEmail();
  };

  return (
    <form onSubmit={onSubmitHandler}>
      <div className="control-group">
        {!firstNameIsInvalid && (
          <div className="form-control">
            <label htmlFor="name">First Name</label>
            <input type="text" id="name" value={firstNameEnteredValue} />
          </div>
        )}
        {!lastNameIsInvalid && (
          <div className="form-control">
            <label htmlFor="name">Last Name</label>
            <input type="text" id="name" value={lastNameEnteredValue} />
          </div>
        )}

        {!emailIsInvalid && (
          <div className="form-control">
            <label htmlFor="name">E-Mail Address</label>
            <input
              type="text"
              id="name"
              value={emailEnteredValue}
              onChange={emailInputChangeHandler}
            />
          </div>
        )}
        <div className="form-actions">
          <button>Submit</button>
        </div>
      </div>
    </form>
  );
};

export default BasicForm;
