import { useState, useReducer } from "react";

const valueReducer = () => {};

const useInput = (validateValue) => {
  const [state, dispatch] = useReducer(valueReducer, {
    enteredValue: "",
    isTouched: false,
  });
  //   const [enteredValue, setEnteredValue] = useState("");
  //   const [isTouched, setIsTouched] = useState(false);

  const valueIsValid = validateValue(enteredValue); // This is run every re-render on each keystroke | First figure out if input is valid
  const hasError = !valueIsValid && isTouched; // inferred value | then check if input has been touched

  const valueChangeHandler = (event) => {
    setEnteredValue(event.target.value); // updates and then schedules so we need to use event.target.value in the if block
  };

  const inputBlurHandler = (event) => {
    setIsTouched(true); // if input looses focused it has been touched
  };

  const reset = () => {
    setEnteredValue("");
    setIsTouched(false);
  };

  return {
    value: enteredValue,
    hasError: hasError,
    isValid: valueIsValid,
    reset: reset,
    valueChangeHandler: valueChangeHandler,
    inputBlurHandler: inputBlurHandler,
  };
};

export default useInput;
