import React, { useState } from "react";

import "./ExpenseFilter.css";

const ExpensesFilter = (props) => {
  //   each dropdown listens and forwards data to expense component

  const [selectedYear, setSelectedYear] = useState("");

  const onChangeHandler = (e) => {
    setSelectedYear(e.target.value);
    // console.log(e.target.value);
    // console.log(selectedYear);
    props.saveExpenseFilter(selectedYear);
  };

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select onChange={onChangeHandler}>
          <option value="2022" onChange={onChangeHandler}>
            2022
          </option>
          <option value="2021" onChange={onChangeHandler}>
            2021
          </option>
          <option value="2020" onChange={onChangeHandler}>
            2020
          </option>
          <option value="2019" onChange={onChangeHandler}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
