import React, { useState, useEffect } from "react";

import MoviesList from "./components/MoviesList";
import Axios from "axios";
import "./App.css";

function App() {
  const DEFAULT_MOVIES = [
    {
      id: 1,
      title: "Some Dummy Movie",
      openingText: "This is the opening text of the movie",
      releaseDate: "2021-05-18",
    },
    {
      id: 2,
      title: "Some Dummy Movie 2",
      openingText: "This is the second opening text of the movie",
      releaseDate: "2021-05-19",
    },
  ];
  const [dummyMovies, setDummyMovies] = useState(DEFAULT_MOVIES);

  const fetchMoviesHandler = () => {
    console.log("here");
    Axios.get("https://swapi.dev/api/films").then((response) => {
      console.log("yoooo: ", response.data.results);
      setDummyMovies(() => response.data.results);
    });
  };
  useEffect(() => {
    // console.log("here");
    // Axios.get("https://swapi.dev/api/people/1/").then((response) => {
    //   console.log("yoooo: ", response.data);
    //   setDummyMovies(() => response);
    // });
    // Axios.get("https://swapi.dev/api/people/1/", (response) => {
    //   // return response.json;
    //   console.log("yoooo: ", response);
    // });
    // .then((response) => {setDummyMovies(() => response)}).catch((error) => {console.log(error)});
    // return () => {};
  }, []);

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      {/* <section>{dummyMovies.map((movi))}</section> */}
      <section>
        <MoviesList movies={dummyMovies} />
      </section>
    </React.Fragment>
  );
}

export default App;
