import React, { useState } from "react";
import "./ExpenseForm.css";

import "../Expenses/ExpenseItem";
import ExpenseItem from "../Expenses/ExpenseItem";
import Card from "../UI/Card";

const ExpenseForm = (props) => {
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

  const [isAddExpenseShown, setIsAddExpenseShown] = useState(false);

  // const [userInput, setUserInput] = useState({
  //   enteredTitle: "",
  //   enteredAmount: "",
  //   enteredDate: "",
  // });

  const titleChangeHandler = (e) => {
    console.log("Title Changed");
    console.log(e.target.value);
    setEnteredTitle(e.target.value);

    // if you rely on prev state use arrow function for atomicity
    // setUserInput((prevState) => {
    //   return {
    //     ...prevState,
    //     enteredTitle: e.target.value,
    //   };
    // });
  };

  const amountChangeHandler = (e) => {
    console.log(e.target.value);
    setEnteredAmount(e.target.value);

    // setUserInput({
    //   ...userInput,
    //   enteredAmount: e.target.value,
    // });
  };

  const dateChangeHandler = (e) => {
    console.log(e.target.value);
    setEnteredDate(e.target.value);
    // setUserInput({
    //   ...userInput,
    //   enteredDate: e.target.value,
    // });
  };

  const submitHandler = (e) => {
    e.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };

    console.log(expenseData);

    props.onSaveExpenseData(expenseData);

    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
    setIsAddExpenseShown(!isAddExpenseShown);
  };

  const addNewExpenseHandler = () => {
    setIsAddExpenseShown(!isAddExpenseShown);
    console.log(isAddExpenseShown);
  };

  let addNewExpense = (
    <button onClick={addNewExpenseHandler}> Add New Expense </button>
  );
  if (isAddExpenseShown === True) {
    addNewExpense = (
      <div className="new-expense__controls">
        <div className="new-expense__controls">
          <label>Title</label>
          <input
            type="text"
            value={enteredTitle}
            onChange={titleChangeHandler}
          />
        </div>

        <div className="new-expense__controls">
          <label>Amount</label>
          <input
            type="number"
            mim="0.01"
            step="0.01"
            value={enteredAmount}
            onChange={amountChangeHandler}
          />
        </div>

        <div className="new-expense__controls">
          <label>Date</label>
          <input
            type="date"
            mim="2019-01-01"
            max="2022-12-31"
            value={enteredDate}
            onChange={dateChangeHandler}
          />
        </div>

        <div className="new-expense__actions">
          <button type="submit">Add Expense</button>
        </div>
      </div>
    );
  }

  return <form onSubmit={submitHandler}>{addNewExpense}</form>;
};
export default ExpenseForm;
