import React, { useState } from "react";
import styles from "./AddUser.module.css";
import Button from "../Button/Button";
import ErrorModal from "../ErrorModal/ErrorModal";

const AddUser = (props) => {
  const [userName, setUserName] = useState("");
  const [age, setAge] = useState("");
  const [ageErrorExists, setAgeErrorExists] = useState(false);
  const [userNameErrorExists, setUserNameErrorExists] = useState(false);

  const userNameChangeHandler = (e) => {
    console.log("userName Updated", e.target.value);
    setUserName(e.target.value);
  };

  const ageChangeHandler = (e) => {
    console.log("age Updated", e.target.value);
    setAge(e.target.value);
  };

  const submitHandler = () => {
    console.log("1,2,3 submitHandler Validation");
    if (age <= 0) {
      console.log("AGE MUST BE GREATER THAN 0");
      setUserNameErrorExists(true);
      // return <h1> AGE MUST BE GREATER THAN 0</h1>;
      // return <ErrorModal />;
    }

    // If username entry is null, throw error modal
    if (userName.trim().length <= 0) {
      setAgeErrorExists(true);
      console.log("AGE MUST BE GREATER THAN 0");
      // return <h1>USER NAME CAN'T BE NULL</h1>;
      return <ErrorModal />;
    }

    const addUserData = {
      userName: userName,
      age: age,
    };

    console.log(addUserData);
    props.onSaveUserData(addUserData);
  };

  return (
    <div>
      {!userNameErrorExists && !ageErrorExists ? (
        <ErrorModal />
      ) : (
        <form>
          <div>
            <label> Username: </label>
            <input
              type="text"
              className={styles.input}
              placeholder="Enter Username Here..."
              onChange={userNameChangeHandler}
            />
          </div>
          <div>
            <label className={styles.label}> Age: </label>
            <input
              type="number"
              className={styles.input}
              placeholder="Enter Age Here..."
              onChange={ageChangeHandler}
            />
          </div>
          <Button onClickHandler={submitHandler} />
        </form>
      )}
      ,
    </div>
  );
};

export default AddUser;
