import React from "react";
import styles from "./ErrorModal.module.css";

const ErrorModal = ({ nameError, ageError }) => {
  return (
    <div>
      <h1> Error</h1>

      <h1>
        {" "}
        {!nameError
          ? "Name field is empty"
          : !ageError
          ? "Age must be > 0."
          : ""}
      </h1>

      <button>Cancel</button>
    </div>
  );
};

export default ErrorModal;
