import React from "react";
import styles from "./ErrorModal.module.css";
import Card from "../UI/Card";
import Button from "../Button/Button";

const Backdrop = ({ onCloseModalHandler }) => {
  return <div className={styles.backdrop} onClick={onCloseModalHandler} />;
};

const ModalOverlay = ({ title, message, onCloseModalHandler }) => {
  return (
    <Card className={styles.modal}>
      <header className={styles.header}>
        <h1> {title} </h1>
      </header>

      <div className={styles.content}>
        <p className={styles.content}>{message}</p>
      </div>
      <footer className={styles.actions}>
        <Button
          type="submit"
          onClick={onCloseModalHandler}
          className={styles.actions}
        >
          Ok
        </Button>
      </footer>
    </Card>
  );
};

const ErrorModal = ({ title, message, onCloseModalHandler }) => {
  return (
    <React.Fragment>
      <div className={styles.backdrop} onClick={onCloseModalHandler}>
        {/* {console.log(!nameError, !ageError)} */}
        <Card className={styles.modal}>
          <header className={styles.header}>
            <h1> {title} </h1>
          </header>

          <div className={styles.content}>
            <p className={styles.content}>{message}</p>
          </div>
          <footer className={styles.actions}>
            <Button
              type="submit"
              onClick={onCloseModalHandler}
              className={styles.actions}
            >
              Ok
            </Button>
          </footer>
        </Card>
      </div>
    </React.Fragment>
  );
};

export default ErrorModal;
