import React, { useState } from "react";
import styles from "./AddUser.module.css";
import Button from "../Button/Button";
import ErrorModal from "../ErrorModal/ErrorModal";

const AddUser = (props) => {
  const [userName, setUserName] = useState("");
  const [age, setAge] = useState("");
  const [ageErrorExists, setAgeErrorExists] = useState(false);
  const [userNameErrorExists, setUserNameErrorExists] = useState(false); // true or false need to determine?
  const [isModalShown, setIsModalShown] = useState(false);

  const userNameChangeHandler = (e) => {
    console.log("userName Updated", e.target.value);
    setUserName(e.target.value);
  };

  const ageChangeHandler = (e) => {
    console.log("age Updated", e.target.value);
    setAge(e.target.value);
  };

  const submitHandler = (e) => {
    console.log("1,2,3 submitHandler Validation");

    // If username entry is null, throw error modal
    if (userName.trim().length <= 0 && (age < 0 || age === "")) {
      setUserNameErrorExists(true);
      setAgeErrorExists(true);
      setIsModalShown(true);
      console.log("NAME CAN'T BE EMPTY and AGE MUST BE GREATER THAN 0");
      return;
    } else if (userName.trim().length <= 0) {
      setUserNameErrorExists(true);
      setIsModalShown(true);
      console.log("NAME CAN'T BE EMPTY");
      return;
    } else if (age < 0 || age === "") {
      console.log("AGE MUST BE GREATER THAN 0");
      setAgeErrorExists(true);
      setIsModalShown(true);
      return;
    }
    const addUserData = {
      userName: userName,
      age: age,
    };

    console.log(addUserData);
    setAgeErrorExists(false);
    setUserNameErrorExists(false);
    setUserName("");
    setAge(0);

    props.onSaveUserData(addUserData);
  };

  const closeModalHandler = () => {
    setIsModalShown(false);
    console.log("in cancel handler " + isModalShown);
  };

  return (
    <div>
      {console.log("here " + !isModalShown)}
      {console.log(
        "condition: " + !isModalShown && !userNameErrorExists && !ageErrorExists
      )}
      {!isModalShown && !userNameErrorExists && !ageErrorExists ? (
        <form>
          <div>
            <label> Username: </label>
            <input
              type="text"
              className={styles.input}
              placeholder="Enter Username Here..."
              onChange={userNameChangeHandler}
            />
          </div>
          <div>
            <label className={styles.label}> Age: </label>
            <input
              type="number"
              className={styles.input}
              placeholder="Enter Age Here..."
              onChange={ageChangeHandler}
            />
          </div>
          <Button onClickHandler={submitHandler} />
        </form>
      ) : (
        <div>
          {console.log(userNameErrorExists, ageErrorExists)}

          <ErrorModal
            nameError={userNameErrorExists}
            ageError={ageErrorExists}
            onCloseModalHandler={closeModalHandler}
          />
        </div>
      )}
    </div>
  );
};

export default AddUser;
