import React from "react";
import styles from "./ErrorModal.module.css";

const ErrorModal = ({ nameError, ageError }) => {
  const error = <h1> Error </h1>;
  if (!nameError && !ageError) {
    error = <h1> Name field can't be empty and Age must be > 0.</h1>;
  } else if (!ageError) {
    error = <h1>Age must be > 0.</h1>;
  } else if (!nameError) {
    error = <h1>Name field is empty </h1>;
  }
  return (
    <div>
      <h1> Error</h1>
      <h1>
        {!nameError && !ageError
          ? "Name field can't be empty and Age must be > 0."
          : ""}
      </h1>

      <h1> {!nameError ? "Name field is empty" : ""}</h1>
      <h1> {!ageError ? "Age must be > 0." : ""}</h1>
    </div>
  );
};

export default ErrorModal;
