import { Component } from "react";
import classes from "./User.module.css";

class User extends Component {
  constructor() {
    super();
    this.state = {
      showUsers: true,
    };
  }
  toggleUsersHandler = () => {
    this.setState(false);
  };

  render() {
    return <li className={classes.user}>{this.props.name}</li>;
  }
}
// const User = (props) => {
